export const direction: Property = 'http://www.w3.org/1999/02/22-rdf-syntax-ns#direction';

/**
 * Property
 * 
 * The class of RDF properties.
 *
 * http://www.w3.org/1999/02/22-rdf-syntax-ns#Property
 */
